mapper.select:
select board_no , board_title , count , to_char(create_date, 'YYYY-MM-DD') as "create_date" , board_writer from board b join member on(board_writer = user_no) where b.status = 'Y' and blind_sta = 'N'
and user_no = #{userNo}
order by board_no desc

mapper.insert:
insert
  into board
  (
    board_no
  , board_writer
  , board_title
  , board_content
  )
  values
  (
    seq_bno.nextval
  , #{boardWriter}
  , #{boardTitle}
  , #{boardContent}
  )

mapper.update:
update 
       board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
     , modify_date = sysdate
 where board_no = #{boardNo}

