mapper.update:
update
       member
   set conn_sta = #{connSta}
 where user_no = #{userNo}

mapper.insert:
insert
  into chat_like
  (
    myuser_no
  , likeuser_no
  )
  values
  (
    #{userNo}
  , #{chatLike}
  )

mapper.delete:
delete
  from chat_like
 where myuser_no = #{userNo}
   and likeuser_no = #{chatLike}

mapper.select:
select cr.room_no , room_name , group_count , (select notread_chat from participant where user_no = 5 and room_no = cr.room_no) "notread_chat" , chat_content , to_char(send_date, 'YYYY.MM.DD PM HH:mm') "send_date" , user_name , profile_img from chatroom cr join participant cp on(cr.room_no = cp.room_no) join chat c on(cr.last_chat = c.chat_no) join member m on(m.user_no = cp.user_no) where cp.room_no in (select room_no from participant where user_no = #{participantNo}) and cp.user_no != #{participantNo}
and (room_name like '%' || #{roomName} || '%' or room_no in (select room_no from participant join member using(user_no) where user_name like '%' || #{roomName} || '%'))
order by chat_no desc
