mapper.update:
update
       member
   set conn_sta = #{connSta}
 where user_no = #{userNo}

mapper.insert:
insert
  into chat_like
  (
    myuser_no
  , likeuser_no
  )
  values
  (
    #{userNo}
  , #{chatLike}
  )

mapper.delete:
delete
  from chat_like
 where myuser_no = #{userNo}
   and likeuser_no = #{chatLike}
