mapper.insert:
insert
  into document
  (
  	doc_no
  , doc_type
  , doc_name
  , save_path
  , origin_name
  , create_user
  )
  values
  (
  	seq_docno.nextval
  , #{docType}
  , #{docName}
  , #{savePath}
  , #{originName}
  , #{createUser}
  )

mapper.update:
update
	   document
   set doc_name = #{docName}
     , save_path = #{savePath}
     , origin_name = #{originName}
     , create_user = #{createUser}
 where doc_no = #{docNo}
