mapper.insert:
insert
  into mail
  (
    mail_no
  , sender
  , sender_mail
  , recipient_mail
  , ref_mail
  , hid_ref_mail
  , mail_title
  , mail_content
  )
  values
  (
    seq_mailno.nextval
  , #{sender}
  , #{senderMail}
  , #{recipientMail}
  , #{refMail}
  , #{hidRefMail}
  , #{mailTitle}
  , #{mailContent}
  )

insert
  into mail_status
  (
    mail_no
  , sender_mail
  , recipient_mail
  , mail_type
  , important_status
  )
  values
  (
    seq_mailno.currval
  , #{senderMail}
  , #{recipientMail}
  , #{mailType}
  , #{importantStatus}
  )

mapper.select:
select count(read_date)
  from mail_status
 where recipient_mail = #{recipientMail}
   and mail_no = #{mailNo}

mapper.update:
update
	   mail_status
   set read_date = sysdate
 where mail_no = #{ mailNo }
   and recipient_mail = #{ recipientMail }

update
	   mail_status
   set important_status = 'N'
  where (recipient_mail = #{recipientMail}
     or sender_mail = #{senderMail})
    and mail_no = #{mailNo}
    and mail_type = #{mailType}
    and important_status = 'Y'

update
	   mail_status
   set important_status = 'Y'
  where (recipient_mail = #{recipientMail}
     or sender_mail = #{senderMail})
    and mail_no = #{mailNo}
    and mail_type = #{mailType}
    and important_status = 'N'

update
       mail_status
   set bin_status = 'Y',
       delete_date = sysdate + 10
 where recipient_mail = #{recipientMail}
   and mail_no = #{mailNo}

update
	   mail_status
   set read_date = null
 where mail_no = #{mailNo}
   and recipient_mail = #{recipientMail}
